<script setup>
import { ref } from 'vue';
import { userLogin } from 'src/api/auth';
import { useAuthStore } from 'stores/auth-store';
import { storeToRefs } from 'pinia';
import { useRouter } from 'vue-router';

// routing
const router = useRouter();

// use authStore
const { accessToken, nickname, isAuthenticated } = storeToRefs(useAuthStore());
const setPositiveAuthInfo = (nick, token, boolean) => {
  nickname.value = nick;
  accessToken.value = token;
  isAuthenticated.value = boolean;
  loginStatus.value = true;
  loginModalContent.value = `Welcome  ${nickname.value}`;
};
const setNegativeAuthInfo = () => {
  nickname.value = null;
  accessToken.value = null;
  isAuthenticated.value = false;
};

// hide password
const isPwd = ref(true);

// User Login
const userLoginData = ref({
  email: 'FUCK5@com',
  password: 'asdf'
});
const userLoginRequest = async () => {
  try {
    const jsonData = {
      email: userLoginData.value.email,
      password: userLoginData.value.password
    };
    const { status, headers, request, data } = await userLogin(jsonData);
    const authHeader = headers['authorization'];

    setPositiveAuthInfo(data.nickname, authHeader.split('Bearer ')[1], true); //TODO: authStore 로 빼줄까생각해보자

    setTimeout(() => {
      loginStatus.value = false;
    }, 1200);

    setTimeout(() => {
      loginStatus.value = false;
      router.push({ name: 'Index' });
    }, 1500);
  } catch (error) {
    console.log('failed');
    setNegativeAuthInfo(); //TODO: authStore 로 빼줄까생각해보자
  }
};

// loginStatus (Modal)
const loginStatus = ref(false);
const loginModalContent = ref('');
</script>

